import React, { useState } from 'react';
import About from '../About';
import Home from '../Home';

const Information = () => {
    const [activeStep, setActiveStep] = useState(1);

    const steps = [
        {
            id: 1,
            content: (
                <div>
                    <p>Paragraph/Medium/Semibold</p>
                    <p className="text-[13px]">Provide details about this RFQ</p>
                </div>
            ),
            component: <About />
        },
        {
            id: 2,
            content: (
                <div>
                    <p>Paragraph/Medium/Semibold</p>
                    <p className="text-[13px]">Payment and delivery terms</p>
                </div>
            ),
            component: <Home />
        },
        {
            id: 3,
            content: (
                <div>
                    <p>Paragraph/Medium/Semibold</p>
                    <p className="text-[13px]">Confirm all information provided</p>
                </div>
            ),
            component: 'Content for Step 3'
        }
    ];

    const handleStepClick = (stepId) => {
        setActiveStep(stepId);
    };

    return (
        <div className="flex flex-col items-center mt-10">
            <div className="flex items-center space-x-8 mb-6">
                {steps.map((step) => (
                    <div key={step.id} className="flex flex-col items-center">
                        <div>
                            <button
                                className={`w-12 h-12 rounded-full flex items-center justify-center text-white font-bold transition-all 
                                    ${activeStep === step.id
                                        ? 'bg-blue-500'
                                        : activeStep > step.id
                                            ? 'bg-lemon-300'
                                            : 'bg-gray-300'
                                    }
                                `}
                                onClick={() => handleStepClick(step.id)}
                            >
                                {step.id}
                            </button>
                        </div>

                        <div className="mt-2 text-sm font-medium text-gray-700">
                            Step {step.id}
                        </div>
                    </div>
                ))}
            </div>
            <div className="w-3/4 p-4 border rounded shadow-md text-center">
                <p className="text-lg text-gray-800">
                    {steps.find((step) => step.id === activeStep)?.component}
                </p>
            </div>
        </div>
    );
};

export default Information;
