import React, { useState } from 'react';
import About from '../About';
import Home from '../Home';

const Information = () => {
    const [activeStep, setActiveStep] = useState(1);

    const steps = [
        { id: 1, p1: 'Request Information', p2: 'Provide Details about RFQ', content: <About /> },
        { id: 2, p3: 'Terms and Attachments', p4: 'Payment and delivery terms', content: <Home /> },
        { id: 3, p5: 'Review', p6: 'Confirm all information provided', content: 'Content for Step 3' },
    ];

    const handleStepClick = (stepId) => {
        setActiveStep(stepId);
    };

    return (
        <div className="flex flex-col items-center mt-10">
            {/* Step navigation buttons */}
            <div className="flex items-center space-x-8 mb-6">
                {steps.map((step) => (
                    <div key={step.id} className="flex flex-col items-center">
                        {/* Button for each step */}
                        <div className="flex gap-2 items-center justify-center">
                            <button
                                className={`w-12 h-12 rounded-full flex items-center justify-center text-white font-bold transition-all
                                ${activeStep === step.id
                                        ? 'bg-blue-500'
                                        : activeStep > step.id
                                            ? 'bg-yellow-300'
                                            : 'bg-gray-300'
                                    }`}
                                onClick={() => handleStepClick(step.id)}
                            >
                                {step.id}
                            </button>

                            {/* Text below the button for each step */}
                            <div className="mt-2 text-sm font-medium text-gray-700">
                                {step.id === 1 && (
                                    <>
                                        <p className='font-bold'>{step.p1}</p>
                                        <p className='text-[13px]'>{step.p2}</p>
                                    </>
                                )}
                                {step.id === 2 && (
                                    <>
                                        <p className='font-bold'>{step.p3}</p>
                                        <p className='text-[13px]'>{step.p4}</p>
                                    </>
                                )}
                                {step.id === 3 && (
                                    <>
                                        <p className='font-bold'>{step.p5}</p>
                                        <p className='text-[13px]'>{step.p6}</p>
                                    </>
                                )}
                            </div>
                        </div>
                    </div>
                ))}
            </div>

            {/* Display content for the active step */}
            <div className="w-3/4 p-4 border rounded shadow-md text-center">
                {/* Conditionally render content based on the active step */}
                {typeof steps.find((step) => step.id === activeStep)?.content === 'string'
                    ? <p className="text-lg text-gray-800">{steps.find((step) => step.id === activeStep)?.content}</p>
                    : steps.find((step) => step.id === activeStep)?.content
                }
            </div>
        </div>
    );
};

export default Information;
