import React, { useState } from 'react';
import About from '../About';
import Home from '../Home';

const Information = () => {
    const [activeStep, setActiveStep] = useState(1);

    const steps = [
        { id: 1, p1: 'Paragraph/Medium/Semibold', p2: 'Paragraph/XSmall/Regular', content: <About /> },
        { id: 2, p3: 'Paragraph/Medium/Semibold', p4: 'Paragraph/XSmall/Regular', content: <Home /> },
        { id: 3, p5: 'Review', p6: 'Paragraph/XSmall/Regular', content: 'Content for Step 3' },
    ];

    const handleStepClick = (stepId) => {
        setActiveStep(stepId);
    };

    return (
        <div className="flex flex-col items-center mt-10">
            <div className="flex items-center space-x-8 mb-6">
                {steps.map((step) => (
                    <div key={step.id} className="flex flex-col items-center">
                        <div className="flex gap-2 items-center justify-center">
                            <button
                                className={`w-12 h-12 rounded-full flex items-center justify-center text-white font-bold transition-all
                                ${activeStep === step.id
                                        ? 'bg-blue-500'
                                        : activeStep > step.id
                                            ? 'bg-yellow-300' // Updated to 'bg-yellow-300' for better visibility
                                            : 'bg-gray-300'
                                    }`}
                                onClick={() => handleStepClick(step.id)}
                            >
                                {step.id}
                            </button>
                            <div className="mt-2 text-sm font-medium text-gray-700">
                                <p>{step.p1}</p>
                                <p>{step.p2}</p>
                            </div>
                        </div>


                    </div>
                ))}
            </div>
            <div className="w-3/4 p-4 border rounded shadow-md text-center">
                <p className="text-lg text-gray-800">
                    {steps.find((step) => step.id === activeStep)?.content}
                </p>
            </div>
        </div>
    );
};

export default Information;
