import React, { useState } from 'react';

const Information = () => {
    const [activeStep, setActiveStep] = useState(1);

    const steps = [
        {
            id: 1,
            content: (
                <div>
                    <p className="font-medium">Paragraph/Medium/Semibold</p>
                    <p className="text-[13px]">Provide details about this RFQ</p>
                </div>
            )
        },
        {
            id: 2,
            content: (
                <div>
                    <p className="font-medium">Paragraph/Medium/Semibold</p>
                    <p className="text-[13px]">Payment and delivery terms</p>
                </div>
            )
        },
        {
            id: 3,
            content: (
                <div>
                    <p className="font-medium">Paragraph/Medium/Semibold</p>
                    <p className="text-[13px]">Confirm all information provided</p>
                </div>
            )
        }
    ];

    const handleStepClick = (stepId) => {
        setActiveStep(stepId);
    };

    return (
        <div className="text-center flex justify-center mt-10">
            <div className="flex gap-6 items-center">
                {steps.map((step) => (
                    <div key={step.id} className="flex flex-col items-center">
                        <div>
                            <button
                                className={`w-12 h-12 rounded-full flex items-center justify-center text-white font-bold transition-all
                                    ${activeStep === step.id
                                        ? 'bg-blue-500'
                                        : activeStep > step.id
                                            ? 'bg-yellow-300'  // Lemon color for past steps
                                            : 'bg-gray-300' // Default color for upcoming steps
                                    }`}
                                onClick={() => handleStepClick(step.id)}
                            >
                                {step.id}
                            </button>
                        </div>
                        <div className="mt-2 text-sm font-medium text-gray-700">
                            Step {step.id}
                        </div>
                    </div>
                ))}
            </div>

            <div className="w-3/4 p-4 border rounded shadow-md text-center mt-6">
                <p className="text-lg text-gray-800">
                    {steps.find((step) => step.id === activeStep)?.content}
                </p>
            </div>
        </div>
    );
};

export default Information;
